import React from "react"

class Job extends React.Component {
  render() {
    return (
        <div style={styles.container}>
            <div style={styles.leftColumn}>
                <img style={styles.logo} src={this.props.company_logo} />
            </div>
            <div style={styles.rightColumn}>
                <h3>{this.props.title}</h3>
                <h5>{this.props.company}</h5>
                <a href={this.props.company_url}>{this.props.company_url}</a>
                <p>{this.props.type}</p>
                <p>{this.props.location}</p>
                {this.props.description}
                {this.props.how_to_apply}
            </div>
        </div>
    )
  }
}

const styles = {
    container: {
        padding: 20,
        display: "flex"
    },
    logo:{
      width:200
    },
    leftColumn:{
        width: 200
    },
    rightColumn:{
        margin:20,
        paddingRight:20,
    }
};

export default Job;
