import React from "react"
import Job from "./Job";

const jobList = [
    {
        title: "Full Stack Software Engineer",
        type: "Full time",
        company: "Gartner Inc.",
        location: "Arlington, VA",
        company_url: "http://betacode.tech",
        company_logo: "https://jobs.github.com/rails/active_storage/blobs/eyJfcmFpbHMiOnsibWVzc2FnZSI6IkJBaHBBdE4zIiwiZXhwIjpudWxsLCJwdXIiOiJibG9iX2lkIn19--2bfd2dd7d55405f9d7f63ca530bf2b19edd7c063/2018%20Gartner%20Logo.jpg"
    },
    {
        title: "Senior Fullstack Ruby / JS Developer (Remote or Berlin)",
        type: "Full time",
        company: "GOhiring – Multiposting & Analytics",
        location: "Berlin/Remote",
        company_url: "http://Oneplanet.com",
        company_logo: "https://jobs.github.com/rails/active_storage/blobs/eyJfcmFpbHMiOnsibWVzc2FnZSI6IkJBaHBBc2wzIiwiZXhwIjpudWxsLCJwdXIiOiJibG9iX2lkIn19--da8766532af91cf9fc5ff9bf65acddc5c16a1604/GOhiring.png"
    }
];

class JobList extends React.Component {

    constructor() {
        super();
        this.state = {jobs: jobList};
    }

    componentDidMount() {
        console.log("Component mounted");
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log("Component did update");
    }

    render() {
        return (
            <div style={styles.container}>
                {this.state.jobs.map((job)=> <Job job={job} />)}
            </div>
        );
    }
}

const styles = {
    container: {
        display:"flex"
    }
}

export default JobList;
