import React from "react"
import Job from "./Job";
import jobs from "./jobs"

class JobList extends React.Component {

    constructor() {
        super();
        this.state = {jobs: jobs, currentJob: jobs[0]};
    }

    componentDidMount() {
        console.log("Component mounted");
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log("Component did update");
    }

    onJobClick(job){
        debugger;
        this.setState({
            currentJob: job
        });
        console.log(`Job with name ${job.title} as been clicked`);
    }

    render() {
        return (
            <div style={styles.container}>
                {this.state.jobs.map((job)=> <Job job={job} onJobClick={this.onJobClick}/>)}
            </div>
        );
    }
}

const styles = {
    container: {
        width: "100%",
        display: "grid",
        "grid-template-columns": "auto auto auto auto auto"
    }
};

export default JobList;
