export const LOAD_JOBS = "LOAD_JOBS";

const loadJobs = (jobs) => {
    return {
        type: LOAD_JOBS,
        jobs
    }
};

export const getJobs = (location, description) => {
    return (dispatch) => {
        const url = `https://github-jobs-proxy.appspot.com/positions?description=${encodeURI(description)}&location=${encodeURI(location)}`;

        fetch(url, {
                crossDomain: true,
                method: 'GET'
            }).then(function (response) {
            if (response.ok) {
                const jobs = response.data;
                return dispatch(loadJobs(jobs));
            }
        }).catch(function (error) {
            console.error(error)
        })
    }
};
